mapper.update:
UPDATE tb_delivery_master
SET
vend = CASE ord_id

    WHEN #{info.ordId} THEN #{info.dlvarVend}

END,
invc_id = CASE ord_id

    WHEN #{info.ordId} THEN #{info.invoiceNum}

END,
dexp = CASE ord_id

    WHEN #{info.ordId} THEN #{info.dlvarExpense}

END
WHERE ord_id IN

    #{info.ordId}

UPDATE tb_delivery_master
SET bndl_yn = IF(ord_id IN

    #{ordId}

AND dlvar_id IN

    #{dlvarId}

, 'y', 'n')
WHERE ord_id IN

    #{ordId}
